    def generatePQG(self):
        if int(self.bits.text()) >= 5:
            p, q, g = generate_p_q_g(int(self.bits.text()))
            self.p.setText(str(p))
            self.q.setText(str(q))
            self.g.setText(str(g))

    def generateMutualKey(self):
        if self.p.text() and self.q.text() and self.g.text() and int(self.bits.text()) >= 2:
            private_key_a, private_key_b = randint(2**(int(self.bits.text()) - 1), 2**int(self.bits.text())), \
                                           randint(2**(int(self.bits.text()) - 1), 2**int(self.bits.text()))

            while private_key_a == private_key_b:
                private_key_b = randint(2**(int(self.bits.text())) - 1, 2**int(self.bits.text()))

            public_key_a, public_key_b = powmod(int(self.g.text()), private_key_a, int(self.p.text())),\
                                         powmod(int(self.g.text()), private_key_b, int(self.p.text()))

            mutual_key_ab, mutual_key_ba = powmod(public_key_b, private_key_a, int(self.p.text())), \
                                           powmod(public_key_a, private_key_b, int(self.p.text()))

            self.public_key_a.setText(str(public_key_a))
            self.public_key_b.setText(str(public_key_b))
            self.private_key_a.setText(str(private_key_a))
            self.private_key_b.setText(str(private_key_b))
            self.private_key_ab.setText(str(mutual_key_ab))
            self.private_key_ba.setText(str(mutual_key_ba))

