    def generatePQ(self):
        p = generate_prime(int(self.bits.text()))
        q = generate_prime(int(self.bits.text()))
        while p == q:
            q = generate_prime(int(self.bits.text()))

        self.p.setText(str(p))
        self.q.setText(str(q))

    def calculateKeys(self):
        if self.p.text() and self.q.text():
            public, private = self.rsa.generate_keys(int(self.p.text()), int(self.q.text()))
            self.public_key.setText(str(public))
            self.private_key.setText(str(private))

    def encryptText(self):
        if self.input.toPlainText():
            encrypted_text = self.rsa.encrypt(self.input.toPlainText(), int(self.public_key.toPlainText()),
                                              int(self.p.text()) * int(self.q.text()))
            self.output.setText(encrypted_text)

    def decryptText(self):
        if self.input.toPlainText():
            decrypted_text = self.rsa.decrypt(self.input.toPlainText(), int(self.private_key.toPlainText()),
                                              int(self.p.text()) * int(self.q.text()))
            self.output.setText(decrypted_text)

    def encryptFile(self):
        if self.outputFilePath.text() and self.inputFilePath.text():
            self.rsa.encrypt('', int(self.public_key.toPlainText()), int(self.p.text()) * int(self.q.text()),
                             self.inputFilePath.text(), self.outputFilePath.text())

    def decryptFile(self):
        if self.outputFilePath.text() and self.inputFilePath.text():
            self.rsa.decrypt('', int(self.private_key.toPlainText()), int(self.p.text()) * int(self.q.text()),
                             self.inputFilePath.text(), self.outputFilePath.text())

    def getPath(self, obj):
        path = QFileDialog.getOpenFileName()[0]
        if path[-3:] != 'rsa':
            self.outputFilePath.setText(path + '.rsa')
        else:
            self.outputFilePath.setText(path[:-4])
        obj.setText(path)

